fr:embedded-tex.fr:embedded-tex-body:
\begin {tikzpicture}[diagram]
  \node  (w) {$ \mathcal { E } $};
  \node  [right = of w] (e) {$ \mathcal { F } $};
  \draw  [->,morphism,offset=1.5] (w) to node[swap] (s) {$ f _{*} $} (e);
  \draw  [->,morphism,offset=1.5] (e) to node[swap] (n) {$ f ^{*} $} (w);
  \DrawAdj {n}{s}
\end {tikzpicture}

