  \node  (1) {$E$};
  \node  [right = of 1] (2) {$A'$};
  \node  [right = of 2] (3) {$C$};
  \node  [below = of 1] (4) {$A$};
  \draw  [->,morphism,exists] (4) to (1);
  \draw  [>->,morphism] (1) to node[desc] {$\cong $} (2);
  \draw  [->,morphism] (4) to node[swap] {$\vartheta _{f}$} (2);
  \draw  [->,morphism,offset = -1.5] (2) to node {$\gamma $} (3);
  \draw  [->,morphism,offset = 1.5] (2) to node[swap] {$\gamma '$} (3);
\end {tikzpicture}

