\begin {tikzpicture}[diagram]
  \SpliceDiagramSquare {
    ne = B',
    se = B,
    nw = A',
    nw/style = pullback,
    sw = A,
    east = g,
    east/style = {>->},
    west = f^{*}(g),
    west/style = {>->},
    south = f,
    north = \varepsilon _{g},
  }
  \node  [above left of = nw] (a) {$A$};
  \draw  [->,morphism,bend left] (a) to node {} (ne);
  \draw  [double,morphism,bend right] (a) to node {} (sw);
  \draw  [->,morphism,exists] (a) to node {} (nw);
\end {tikzpicture}

