  \node  [below = of 2] (4) {$V$};
  \draw  [>->,morphism] (2) to node[swap] {$ \nu _{1}$} (1);
  \draw  [>->,morphism] (2) to node {$ \nu _{2}$} (3);
  \draw  [>->,morphism] (1) to node[swap] {$ a ^{*} ( \ell )$} (4);
  \draw  [>->,morphism] (2) to node[desc] {$y$} (4);
  \draw  [>->,morphism] (3) to node {$ b ^{*} (k)$} (4);
\end {tikzpicture}

