   west = {i_{1}},
   west/style = {>->},
   south = {u_{1}},
   south/style = {>->},
   east = {u_{2}},
   east/style = {>->},
 }
  \node  [above left = of nw] (a) {$ \underline {A}$};
  \draw  [->,morphism,bend right] (a) to node[swap] {$ \ell ' \nu _{1}$} (sw);
  \draw  [->,morphism,exists] (a) to node[desc] {$z$} (nw);
