  \SpliceDiagramSquare {
   nw = { \varphi { \mathopen {}[ t ] \mathclose {}} },
   nw/style = pullback,
   ne =  \varphi ,
   sw =  \Gamma ,
   se =  \Delta ,
   south = t,
   east/style = {>->},
   west/style = {>->},
 }
  \node  [above left = of nw] (a) {$s = t  \wedge   \varphi { \mathopen {}[ s ] \mathclose {}} $};
  \draw  [>->,morphism,bend right] (a) to (sw);
  \draw  [->,morphism,bend left] (a) to node {$ba$} (ne);
  \draw  [->,exists,morphism] (a) to (nw);
\end {tikzpicture}

