\begin {tikzpicture}[diagram]
  \SpliceDiagramSquare {
   nw = {0_{A}},
   nw/style = {pullback},
   ne = 0,
   sw = A,
   se = 1,
   west = {q},
   west/style = {>->},
   south = { \mathsf {!} _{ A } },
   east/style = {>->},
   north = {p},
 }
  \node  [above left = of nw] (a) {$A$};
  \node  [left = of nw,pullback] (1) {$0_{A  \times  A}$};
  \node  [below = of 1] (2) {$A  \times  A$};
  \node  [left = of a] (aa) {$A  \times  A$};
  \draw  [->,morphism,out = 0, in = 90] (a) to node {$f$} (ne);
  \draw  [->,morphism,exists] (a) to node[desc] {$s$} (nw);
  \draw  [->,morphism] (1) to (nw);
  \draw  [->,morphism] (2) to node[swap] {$ \pi _{1}$} (sw);
  \draw  [>->,morphism] (1) to (2);
  \draw  [double,morphism,bend right] (aa) to (2);
  \draw  [->,morphism] (aa) to node {$ \pi _{1}$} (a);
  \draw  [->,morphism,exists] (aa) to (1);
\end {tikzpicture}

