  \draw  [>->,morphism] (4) to (5);
  \draw  [>->,morphism] (2) to (5);
  \draw  [->,morphism] (2) to node {$b$} (3);
  \draw  [>->,morphism] (3) to (6);
  \draw  [->,morphism,offset=-1.5] (5) to node {$s$} (6);
  \draw  [->,morphism,offset=1.5] (5) to node[swap] {$t$} (6);
\end {tikzpicture}

