           \SpliceDiagramSquare {
            nw = B',
            nw/style = pullback,
            ne = C',
            sw = B,
            se = C,
            south = g,
            west = n,
            west/style = {>->},
            west/node/style = {desc},
            east = o,
            east/style = {>->},
          }
           \node  [left = of nw, pullback] (A') {$A'$};
           \node  [left = of sw] (A) {$A$};
           \draw  [->,morphism] (A') to (nw);
           \draw  [>->,morphism] (A') to node[swap] {$m$} (A);
           \draw  [->,morphism] (A) to node[swap] {$f$} (sw);
         \end {tikzpicture}




