            width = 3cm,
            nw = {M(A_{1}, \ldots ,A_{n})},
            ne = MB,
            sw = {N(A_{1}, \ldots ,A_{n})},
            se = NB,
            north = Mf,
            south = Nf,
            west = {h_{A_{1}}  \times   \cdots   \times  h_{A_{n}}},
            east = {h_{B}},
          }



