fr:embedded-tex.fr:embedded-tex-body:
\begin {tikzpicture}[diagram]
  \SpliceDiagramSquare {
   nw = A,
   nw/style = {pullback},
   ne = B,
   sw = C,
   se = D,
   north = h,
   south = g,
   west = k,
   east = f,
 }
  \node  [left = of nw,pullback] (1) {$A'$};
  \node  [left = of sw] (2) {$C'$};
  \draw  [>->,morphism] (2) to (sw);
  \draw  [>->,morphism] (1) to (nw);
  \draw  [->,morphism] (1) to (2);
\end {tikzpicture}

