\begin {tikzpicture}[diagram]
  \SpliceDiagramSquare {
   nw = B',
   nw/style = {pullback},
   ne = B,
   sw = A',
   se = C,
   north = m',
   north/style = {>->},
   south = m,
   south/style = {>->},
   west = g',
   east = g,
   east/style = {cover},
 }
  \node  [above left = of nw] (a) {$A$};
  \draw  [->,morphism,bend right] (a) to node[swap] {$n$} (sw);
  \draw  [->,morphism,exists] (a) to (nw);
  \draw  [cover,morphism,bend left] (a) to node {$f$} (ne);
\end {tikzpicture}

