\begin {tikzpicture}[diagram]
  \node  (1) {$R$};
  \node  [right = of 1] (2) {$A  \times  A$};
  \node  [below = of 1] (3) {$A$};
  \draw  [>->,morphism] (1) to node {$(a,b)$} (2);
  \draw  [->,morphism] (3) to node {$ \rho $} (1);
  \draw  [>->,morphism] (3) to node[swap] {$ \Delta $} (2);
\end {tikzpicture}

