  \node  (1) {$R$};
  \node  [right = of 1] (2) {$A  \times  A$};
  \node  [below = of 1] (3) {$R  \times _{A} R$};
  \draw  [>->,morphism] (1) to node {$(a,b)$} (2);
  \draw  [->,morphism] (3) to node {$ \tau $} (1);
  \draw  [>->,morphism] (3) to node[swap] {$(ap,bq)$} (2);
\end {tikzpicture}

