\begin {tikzpicture}[diagram]
  \node  (1) {$E$};
  \node  (2) [right = of 1] {$B \times  A$};
  \node  (3) [right = of 2] {$B$};
  \node  (4) [below = of 2] {$I$};
  \draw  [>->,morphism] (1) to node {$e$} (2);
  \draw  [->,morphism] (2) to node {$\pi _{1}$} (3);
  \draw  [cover,morphism] (1) to (4);
  \draw  [>->,morphism] (4) to node[swap] {$\mathsf {Img}(b)$} (3);
\end {tikzpicture}


