  \node  (1) {$A \times  A$};
  \node  (2) [right = of 1] {$A$};
  \node  (3) [left = of 1] {$A$};
  \node  (4) [below = of 3] {$E$};
  \draw  [->,morphism,offset=-1.5] (1) to node {$\pi _{1}$} (2);
  \draw  [->,morphism,offset=1.5] (1) to node[swap] {$\pi _{2}$} (2);
  \draw  [>->,morphism] (3) to node {$\Delta $} (1);
  \draw  [>->,morphism] (4) to node[swap] {$e$} (1);
  \draw  [->,morphism,exists] (4) to (3);
\end {tikzpicture}


