    nw = {\varphi {\mathopen {}[t]\mathclose {}}},
    nw/style = pullback,
    ne = \varphi ,
    sw = \Gamma ,
    se = \Delta ,
    south = t,
    east/style = {>->},
    west/style = {>->},
