\begin {tikzpicture}[diagram]
  \node  (e) {$E$};
  \node  [right = of e] (0) {$0$};
  \node  [right = of 0] (a) {$A$};
  \draw  [>->,morphism] (e) to node {$e$} (0);
  \draw  [->,morphism,offset=-1.5] (0) to node {$a$} (a);
  \draw  [->,morphism,offset=1.5] (0) to node[swap] {$b$} (a);
\end {tikzpicture}


