  \SpliceDiagramSquare {
    nw = C,
    nw/style = {pullback},
    ne = A,
    sw = A,
    se = B,
    north = q,
    west = p,
    south = f,
    east = f,
  }
  \node  [above left = of nw] (aa) {$\AA $};
  \draw  [->,morphism,bend right] (aa) to node[swap] {$m$} (sw);
  \draw  [->,morphism,bend left] (aa) to node {$n$} (ne);
  \draw  [->,morphism,exists] (aa) to node[desc] {$s$} (nw);
\end {tikzpicture}


