fr:embedded-tex.fr:embedded-tex-body:
\begin {tikzpicture}[diagram]
  \SpliceDiagramSquare {
   nw = C,
   ne = A,
   sw =  \underline {B'},
   se = B,
   north =  f ^{*} ( \mathsf { Img } (g)),
   east = f,
   south =  \mathsf { Img } (g),
   north/style = {>->},
   south/style = {>->},
   nw/style = pullback,
 }
  \node  [left = of nw,pullback] (1) {$D$};
  \node  [left = of sw] (2) {$B'$};
  \draw  [cover,morphism] (1) to node {$ \widehat { \vartheta _{g}}$} (nw);
  \draw  [->,morphism] (1) to (2);
  \draw  [cover,morphism] (2) to node[swap] {$ \vartheta _{g}$} (sw);
\end {tikzpicture}

