  \SpliceDiagramSquare {
   nw = {A_{1}  \cap  A_{2}},
   nw/style = {pullback},
   ne = {A_{2}},
   sw = {A_{1}},
   se = {A_{1}  \cup  A_{2}},
   north = {i_{2}},
   north/style = {>->},
   west = {i_{1}},
   west/style = {>->},
   south = {u_{1}},
   south/style = {>->},
   east = {u_{2}},
   east/style = {>->},
 }
  \node  [above left = of nw] (a) {$ \underline {A}$};
  \draw  [->,morphism,bend right] (a) to node[swap] {$ \ell ' \nu _{1}$} (sw);
  \draw  [->,morphism,exists] (a) to node[desc] {$z$} (nw);
  \draw  [->,morphism,bend left] (a) to node {$k'' \nu _{2}$} (ne);
\end {tikzpicture}

