\begin {tikzpicture}[diagram]
  \node  (1) {$A$};
  \node  (2) [right = of 1] {$B \times  A$};
  \node  (3) [right = of 2] {$B$};
  \draw  [>->,morphism] (1) to node {$(f,\mathsf {id}_{A})$} (2);
  \draw  [->,morphism,offset=-1.5] (2) to node {$f\pi _{2}$} (3);
  \draw  [->,morphism,offset=1.5] (2) to node [swap] {$\pi _{1}$} (3);
\end {tikzpicture}

