fr:embedded-tex.fr:embedded-tex-body:
  \begin {tikzpicture}[diagram, node distance=2cm and 5cm]
    \node  (l1) {$(FX  \otimes  FY)  \otimes  FZ$};
    \node  [below= of l1] (l2) {$F(X  \otimes  Y)  \otimes  FZ$};
    \node  [below= of l2] (l3) {$F((X  \otimes  Y)  \otimes  Z)$};
    \node  [right= of l1] (r1) {$FX  \otimes  (FY  \otimes  FZ)$};
    \node  [below= of r1] (r2) {$FX  \otimes  F(Y  \otimes  Z)$};
    \node  [below= of r2] (r3) {$F(X  \otimes  (Y  \otimes  Z))$};
    \begin {morphism}
      \draw  [->] (l1) to node [left] {$ \phi ^{2} _{ X , Y }   \otimes  FZ$} (l2);
      \draw  [->] (l2) to node [left] {$ \phi ^{2} _{ X  \otimes  Y , Z } $} (l3);
      \draw  [->] (l1) to node [above] {$ \alpha _{ 
  \mathsf { D } 
}$} (r1);
      \draw  [->] (r1) to node [right] {$FX  \otimes   \phi ^{2} _{ Y , Z } $} (r2);
      \draw  [->] (r2) to node [right] {$ \phi ^{2} _{ X , Y  \otimes  Z } $} (r3);
      \draw  [->] (l3) to node [below] {$F \alpha _{ 
  \mathsf { C } 
}$} (r3);
    \end {morphism}
  \end {tikzpicture}

