 \NewDocumentCommand \CreateRect {D<>{} m m}{
   \path 
    coordinate (#1nw)
    ++(#2,-#3) coordinate (#1se)
    coordinate (#1sw) at (#1se -| #1nw)
    coordinate (#1ne) at (#1nw -| #1se)
  ;

   \path [spath/save = #1north] (#1nw) to (#1ne);
   \path [spath/save = #1west] (#1nw) to (#1sw);
   \path [spath/save = #1east] (#1ne) to (#1se);
   \path [spath/save = #1south] (#1sw) to (#1se);
}


  \usepackage{tikz, tikz-cd, mathtools, amssymb, stmaryrd, quiver}
\usetikzlibrary{matrix,arrows}
\usetikzlibrary{backgrounds,fit,positioning,calc,shapes}
\usetikzlibrary{decorations.pathreplacing}
\usetikzlibrary{decorations.pathmorphing}
\usetikzlibrary{decorations.markings}

% basic settings
\tikzset{
  diagram/.style = {
    on grid,
    node distance=2cm,
    commutative diagrams/every diagram,
    line width = .5pt,
    every node/.append style = {
      commutative diagrams/every cell,
    },
  },
  morphism/.style = {
    auto=left,
    font=\scriptsize,
  },
  desc/.style = {anchor=center,fill=white,inner sep=3pt},
  % /tikz/pos=<fraction> is a key already
  offset/.style args = {#1} {commutative diagrams/shift right={#1}},
}

% pullback and pushout
\tikzset{
  pullback/.style = {
    append after command = {
      \pgfextra{
        \draw ($(\tikzlastnode) + (.2cm,-.5cm)$) -- ++(0.3cm,0) -- ++(0,0.3cm);
      }
    }
  },
  pushout/.style = {
    append after command={
      \pgfextra{
        \draw ($(\tikzlastnode) + (-.2cm,.5cm)$) -- ++(-0.3cm,0) -- ++(0,-0.3cm);
      }
    }
  },
}

% arrow styles
\tikzset{
  fibration/.style = {
    -{Triangle[open]}
  },
  etale/.style = {
    -{Triangle[open]}
  },
  etale cover/.style= {
    >={Triangle[open]},->.>
  },
  opfibration/.style = {
    -{Triangle}
  },
  lies over/.style = {
    |-{Triangle[open]}
  },
  op lies over/.style = {
    |-{Triangle}
  },
  embedding/.style = {
    {right hook}->
  },
  open immersion/.style = {
    {right hook}-{Triangle[open]}
  },
  closed immersion/.style = {
    {right hook}-{Triangle}
  },
  closed immersion*/.style = {
    {left hook}-{Triangle}
  },
  embedding*/.style = {
    {left hook}->
  },
  open immersion*/.style = {
    {left hook}-{Triangle[open]}
  },
  exists/.style = {
    densely dashed
  },
  equal/.style = {
    double
  },
}

\newlength{\dontworryaboutit}

\tikzset{
  inline diagram/.style = {
    commutative diagrams/every diagram,
    commutative diagrams/cramped,
    line width = .5pt,
    every node/.append style = {
      commutative diagrams/every cell,
      anchor = base,
      inner sep = 0pt
    },
    every path/.append style = {
      outer xsep = 2pt
    }
  }
}

