    nw = C,
    ne = A,
    sw = \underline {B'},
    se = B,
    north = f^{*}(\mathsf {Img}(g)),
    east = f,
    south = \mathsf {Img}(g),
    north/style = {>->},
    south/style = {>->},
    nw/style = pullback,
  }
  \node  [left = of nw,pullback] (1) {$D$};
  \node  [left = of sw] (2) {$B'$};
