\begin {tikzpicture}[diagram]
  \node  (n) {$A$};
  \node  [below= of n] (s) {$B$};
  \node  [right= of n] (e) {$S$};
  \draw  [morphism,>->] (n) to node [left] {$m$} (s);
  \draw  [morphism,->] (n) to node [above] {$f$} (e);
  \draw  [morphism,->,exists] (s) to node [yshift=-2pt,right] {$g$} (e);
\end {tikzpicture}

