  \node  [below= of l1] (l2) {$F(X \otimes  Y) \otimes  FZ$};
  \node  [below= of l2] (l3) {$F((X \otimes  Y) \otimes  Z)$};
  \node  [right= of l1] (r1) {$FX \otimes  (FY \otimes  FZ)$};
  \node  [below= of r1] (r2) {$FX \otimes  F(Y \otimes  Z)$};
  \node  [below= of r2] (r3) {$F(X \otimes  (Y \otimes  Z))$};
  \draw  [->,morphism] (l1) to node [left] {$\phi ^{2}_{X,Y} \otimes  FZ$} (l2);
  \draw  [->,morphism] (l2) to node [left] {$\phi ^{2}_{X \otimes  Y,Z}$} (l3);
  \draw  [->,morphism] (l1) to node [above] {$\alpha _{\mathcal {D}}$} (r1);
  \draw  [->,morphism] (r1) to node [right] {$FX \otimes  \phi ^{2}_{Y,Z}$} (r2);
  \draw  [->,morphism] (r2) to node [right] {$\phi ^{2}_{X,Y \otimes  Z}$} (r3);
  \draw  [->,morphism] (l3) to node [below] {$F\alpha _{\mathcal {C}}$} (r3);
\end {tikzpicture}

