\begin {tikzpicture}[diagram]
  \node  (1) {$B \times  A'$};
  \node  [right = of 1] (2) {$A$};
  \node  [between = 1 and 2] (ref) {};
  \node  [below = 1.5cm of ref] (3) {$B$};
  \draw  [->,morphism] (1) to node[swap] {$B^{*}(A')$} (3);
  \draw  [->,morphism] (1) to node {$\alpha $} (2);
  \draw  [->,morphism] (2) to node {$f$} (3);
\end {tikzpicture}

