  \SpliceDiagramSquare {
    nw = D',
    nw/style = pullback,
    ne = {{C'}^{A}},
    sw = B,
    se = {B^{A}},
    east = {g^{A}},
    south = {\pi _{2}^{\dag }},
    west = {g^{B^{*}(A)}},
    west/node/style = {right},
  }
  \node  [above left = of nw] (d) {$D$};
  \draw  [->,morphism,bend right] (d) to node[swap] {$f^{B^{*}(A)}$} (sw);
  \draw  [->,morphism,exists] (d) to node[desc] {$\alpha ^{B^{*}(A)}$} (nw);
  \draw  [->,morphism,bend left] (d) to node {$\alpha ^{A}\beta $} (ne);
\end {tikzpicture}

