\begin {tikzpicture}[diagram]
  \SpliceDiagramSquare {
    nw = R,
    nw/style = {pullback},
    ne = A,
    sw = A,
    se = {B},
    north = b,
    west = a,
    south = {f},
    east = {f},
  }
  \node  [above left = of nw] (c) {$C$};
  \draw  [->,morphism,bend right] (c) to node[swap] {$h$} (sw);
  \draw  [->,morphism,exists] (c) to (nw);
  \draw  [->,morphism,bend left] (c) to node {$k$} (ne);
\end {tikzpicture}

