\begin {tikzpicture}[diagram]
  \SpliceDiagramSquare {
    nw = R,
    nw/style = {pullback},
    ne = A,
    sw = A,
    se = B,
    north = b,
    west = a,
    south = f,
    east = f,
  }
  \node  [above left = of nw] (a) {$A$};
  \draw  [->,morphism,bend right] (a) to node[swap] {$\mathsf {id}_{A}$} (sw);
  \draw  [->,morphism,exists] (a) to node[desc] {$\rho $} (nw);
  \draw  [->,morphism,bend left] (a) to node {$\mathsf {id}_{A}$} (ne);
\end {tikzpicture}
\qquad 
\begin {tikzpicture}[diagram]
  \SpliceDiagramSquare {
    nw = R,
    nw/style = {pullback},
    ne = A,
    sw = A,
    se = B,
    north = b,
    west = a,
    south = f,
    east = f,
  }
  \node  [above left = of nw] (r) {$R$};
  \draw  [->,morphism,bend right] (r) to node[swap] {$b$} (sw);
  \draw  [->,morphism,exists] (r) to node[desc] {$\sigma $} (nw);
  \draw  [->,morphism,bend left] (r) to node {$a$} (ne);
\end {tikzpicture}
\qquad 
