    sw = A,
    se = B,
    north = b,
    west = a,
    south = f,
    east = f,
  }
  \node  [above left = of nw] (rr) {$R \times _{A} R$};
  \draw  [->,morphism,bend right] (rr) to node[swap] {$ap$} (sw);
  \draw  [->,morphism,exists] (rr) to node[desc] {$\tau $} (nw);
  \draw  [->,morphism,bend left] (rr) to node {$bq$} (ne);
\end {tikzpicture}

