\begin {tikzpicture}[diagram]
  \SpliceDiagramSquare {
    nw = {0_{A}},
    nw/style = {pullback},
    ne = 0,
    sw = A,
    se = 1,
    west = {q},
    west/style = {>->},
    south = {\mathsf {!}_{A}},
    east/style = {>->},
    north = {p},
  }
  \node  [above left = of nw] (a) {$A$};
  \draw  [double,morphism,bend right] (a) to (sw);
  \draw  [->,morphism,exists] (a) to node[desc] {$s$} (nw);
  \draw  [->,morphism,bend left] (a) to node {$f$} (ne);
\end {tikzpicture}

